mapper.select:
SELECT COUNT(member_id)
FROM member
WHERE member_id = #{member_id}

SELECT COUNT(name)
FROM member
WHERE member_id = #{member_id}
AND password = #{password}

SELECT member_id, password, name, phone, address, isactive, isadmin
FROM member
WHERE member_id = #{member_id}

mapper.update:
UPDATE member
SET password = #{newPassword}, name = #{name}, phone = #{phone}, address = #{address}
WHERE member_id = #{member_id}

UPDATE member
SET isactive = 'n'
WHERE member_id = #{member_id}
AND password = #{password}
