mapper.select:
SELECT COUNT(member_id)
FROM member
WHERE member_id = #{member_id}

SELECT member_id, password, name, phone, address, isactive, isadmin
FROM member
WHERE member_id = #{member_id}

mapper.update:
UPDATE member
SET isactive = 'n'
WHERE member_id = #{member_id}
AND password = #{password}

UPDATE member
SET password = #{newPassword},
name = #{name},
phone = #{phone},
address = #{address}
WHERE member_id = #{member_id}
AND password = #{oldPassword}
