mapper.update:
UPDATE product
SET thumbnail_file_idx = #{thumbnailIdx}
WHERE product_idx = #{productIdx}

mapper.select:
SELECT COUNT(member_id)
FROM hope
WHERE product_idx = #{productIdx}

mapper.delete:
DELETE
FROM hope
WHERE product_idx = #{productIdx} AND member_id = #{member_id}

mapper.select:
SELECT COUNT(*)
FROM comment
WHERE comment_idx = #{commentIdx} AND member_id = #{member_id}

mapper.update:
UPDATE comment
SET content = #{content}, updatetime = now()
WHERE comment_idx = #{commentIdx}

UPDATE comment
SET isview = 'n', updatetime = now()
WHERE comment_idx = #{commentIdx} AND member_id = #{member_id}
