mapper.select:
SELECT *
FROM V_PRODUCT_MAIN



        WHERE small_category_name = #{smallCategoryName}


        WHERE (small_category_name = #{smallCategoryName} AND title LIKE #{findTitle})


LIMIT #{contentNum} OFFSET #{offset}

SELECT COUNT(*)
FROM product
WHERE product_idx = #{productIdx} AND member_id = #{member_id}

